import { DynamicProperty } from '@microsoft/sp-component-base';
import { IConfiguredPropertyPaneDynamicDataProps } from './IPropertyPaneDynamicData';
export interface IDynamicDataWidgetProps extends IConfiguredPropertyPaneDynamicDataProps {
    /** Callback when the dynamic data changes */
    onChange: (targetProperty: string, dynamicProperty: DynamicProperty<{}>) => void;
}
export interface IDynamicDataWidgetState {
    /**
     * Selected source in the dropdown
     */
    sourceId: string;
    selectedValues: {
        [targetProperty: string]: ISelectedValue;
    };
}
export interface ISelectedValue {
    /**
     * Selected property in the dropdown
     */
    property: string;
    /**
     * Current Dynamic Data object. May be undefined if none is selected yet.
     */
    dynamicProperty: DynamicProperty<{}> | undefined;
    label: string;
}
